import React, {Component} from 'react';
import {Form, FormGroup, Row, Col, FormControl, Button} from 'react-bootstrap';
import './Register.css';

class Register extends Component {
   constructor(props) {
      super(props);

      // Current login state
      this.state = {
         firstName: '',
         lastName: '',
         email: '',
         password: '',
         confirmPwd: '',
         grade: '',
         role: 0
      }

       // bind 'this' to the correct context
       this.handleChange = this.handleChange.bind(this);
   } 

   send() {
      let { // Make a copy of the relevant values in current state
         firstName,
         lastName,
         email,
         password,
         verifyPwd,
         grade,
         role
      } = this.state;

      const user = {
         firstName,
         lastName,
         email,
         password,
         verifyPwd,
         grade,
         role
      };

      this.props.register(user, () => {this.setState({offerSignIn: true})});
   }

   validForm(){
      let temp_state = this.state;
      return temp_state.firstName && temp_state.lastName && temp_state.email &&
         temp_state.password === temp_state.verifyPwd;
   }

   // Continually update state as letters typed. Rerenders, but no DOM change!
   handleChange(event) {
      let newState = {}
      switch (event.target.type){
         case 'checkbox':
            if (event.target.id === "role"){
               if (event.target.checked)
                  newState[event.target.id] = 1
               else
                  newState[event.target.id] = 0
            }
            else{
               newState[event.target.id] = event.target.checked;
            }
         default:
            newState[ev.target.id] = ev.target.value;
      }
      this.setState(newState);
   }

   render() {
      console.log("Rendering Register");
      return (
         <section className="container">
            <Col sm={{offset: 2}}>
               <h1>Register</h1>
            </Col>
            <Form>
               <FormGroup as={Row} controlId="formHorizontalEmail">
                  <Col as={Form.Label} sm={2}>
                     Email
                  </Col>
                  <Col sm={8}>
                     <FormControl
                      type="email"
                      name="email"
                      placeholder="Email"
                      value={this.state.email}
                      onChange={this.handleChange}
                      />
                  </Col>
               </FormGroup>
               <FormGroup as={Row} controlId="formHorizontalPassword">
                  <Col as={Form.Label} sm={2}>
                     Password
                  </Col>
                  <Col sm={8}>
                     <FormControl
                      type="password"
                      name="password"
                      placeholder="Password"
                      value={this.state.password}
                      onChange={this.handleChange}
                     />
                  </Col>
               </FormGroup>

               <FormGroup >
                  <Col sm={{offset: 2}}>
                     <Button className="loginbutton" type="submit" onClick={this.logIn}>
                        Sign in
                     </Button>
                 </Col>
               </FormGroup>
            </Form>


         </section>
      )
   }
}

export default Register;
