import React, { Component } from 'react';
import {Route, Redirect, Switch } from 'react-router-dom';
import {Navbar, Nav, NavItem, ListGroup, ListGroupItem} from 'react-bootstrap';
import {LinkContainer} from 'react-router-bootstrap';
import {NavigationBar, Carousel, AboutUs} from '../components';
import './Home.css';

var ProtectedRoute = ({component: Cmp, path, ...rest }) => {
   return (<Route path={path} render={(props) => {
      return Object.keys(rest.User).length !== 0 ?
      <Cmp {...rest}/> : <Redirect to='/signin'/>;}}/>);
   };

class Home extends Component {
   constructor(props){

      super(props);
      this.state= {
         isHidden: true
      }
   }
   // signedIn() {
   //    return Object.keys(this.props.Usr).length !== 0; // Nonempty Prss obj
   // }
/*    state = {
      activeSlideIndex: 0,
   }
   setActiveSlideIndex = (newActiveSlideIndex) => {
      this.setState({
        activeSlideIndex: newActiveSlideIndex
      });
    } */
   render() {
      console.log("Redrawing homepage");
      return (
         <div className="homeNavBar">
            <NavigationBar/>
            {/* <Navbar expand="md">

            <Navbar.Toggle />
                  <Navbar.Collapse className="flex mr-auto">
                     <Nav variant="pills" className="justify-content-left">

                     <LinkContainer to='/about'>
                           <Nav.Link> TESTING</Nav.Link>
                     </LinkContainer>
                     </Nav>
                  </Navbar.Collapse>

            </Navbar>
 */}

         <div>
         <Carousel />

         <br></br>
         </div>
         <div className="hometxt">
         <h1>ABOUT US</h1>
         <p>DeBest robotics is a robotics program associated with the Pegasus School in Huntington Beach, CA.  
            We are teaching young minds the keys to solving problems and how to work with a team to accomplish a common goal.
         </p>
         </div>

            {/*Alternate pages beneath navbar, based on current route*/}
            <Switch>
               <Route exact path='/'
                  component={() => Object.keys(this.props.User).length !== 0 ? 
                     <Redirect to="/signin" /> : <Redirect to="/about" />} />
               <Route path='/about' component={AboutUs} />

            </Switch>

         </div>
      )
   }
}

export default Home
