import React, { Component } from 'react';
import "./Carousel.css";

const imgUrls = [
	"https://lh3.googleusercontent.com/oxPeODS2m6rYIVbhcQChRtOWEYeGDwbeeeB1cDU2o_WYAVPU61VIgx-_6BAh5gSL8Sw=h900",
	"../../public/images/DeBestRoboticsLogo.png", 
	"https://i0.wp.com/www.universodegatos.com/wp-content/uploads/2017/04/fivfelv7.jpg?resize=582%2C328",
	"https://i.pinimg.com/736x/07/c3/45/07c345d0eca11d0bc97c894751ba1b46.jpg",
	"https://ehealthforum.com/health/images/avatars/11699147425707699031013.jpeg"
];

const Arrow = ({ direction, clickFunction, glyph }) => (
	<div 
		className={ `arrow ${direction}` } 
		onClick={ clickFunction }>
		{ glyph } 
	</div>
);

const ImgCarousel = ({ url }) => {
	const styles = {
		backgroundImage: `url(${url})`,
		backgroundSize: 'cover',
		backgroundPosition: 'center'
	};

	return (
		<div className="image-slide" style={styles}></div>
	);
}

export default class Carousel extends Component{
	constructor(props){
		super(props);

		this.state = {
			currImgIndex: 0
		};

		this.nextImg = this.nextImg.bind(this);
		this.previousImg = this.previousImg.bind(this);
	}
	previousImg() {
		const lastIndex = imgUrls.length - 1;
		const {currImgIndex} = this.state;
		const resetIndex = currImgIndex === 0;
		const index = resetIndex ? lastIndex : currImgIndex - 1;

		this.setState({
			currImgIndex: index
		});
	}

	nextImg(){
		const lastIndex = imgUrls.length - 1;
		const {currImgIndex} = this.state;
		const resetIndex = currImgIndex === lastIndex;
		const index = resetIndex ? 0 : currImgIndex + 1;

		this.setState({
			currImgIndex: index
		});
	}

	render(){
		return(
			<div className="carousel">
				<Arrow direction="left" clickFunction={ this.previousImg } glyph="&#9664;"/>
				<ImgCarousel url={imgUrls[this.state.currImgIndex]}/>
				<Arrow direction="right" clickFunction={ this.nextImg } glyph="&#9654;"/>
			</div>
		);
	}

}
